import { useParams } from "react-router-dom";
import NavbarPer from "../navbar/navbar.js";
import PagGastos from "../gastos/gastos.js";
import PagIngresos from "../ingresos/ingresos.js";
import React, { useState, useEffect } from 'react';
import './PaginaPrincipal.css';
import axios from 'axios';

const URICuentasUser = 'http://localhost:8000/cuentas/user/';
const URICuentaDetalle = 'http://localhost:8000/cuentas/';

function PaginaPrincipal() {
  const { idUser } = useParams();

  const [cuentas, setCuentas] = useState([]); // Estado para almacenar las cuentas
  const [selectedCuentaId, setSelectedCuentaId] = useState(null); // Estado para almacenar el ID de la cuenta seleccionada
  const [selectedCuentaNombre, setSelectedCuentaNombre] = useState(''); // Estado para almacenar el nombre de la cuenta seleccionada
  const [mostrarGastos, setMostrarGastos] = useState(true);

  useEffect(() => {
    // Cargar las cuentas al renderizar el componente
    const fetchCuentas = async () => {
      try {
        const response = await axios.get(URICuentasUser + idUser); // Obtener las cuentas del usuario
        setCuentas(response.data); // Actualizar el estado con las cuentas
      } catch (error) {
        console.error(error);
      }
    };
    fetchCuentas();
  }, [idUser]);

  useEffect(() => {
    // Obtener el nombre de la cuenta seleccionada
    const fetchCuentaNombre = async () => {
      if (selectedCuentaId) {
        try {
          const response = await axios.get(URICuentaDetalle + selectedCuentaId);
          setSelectedCuentaNombre(response.data.nombre); // Actualizar el estado con el nombre de la cuenta
        } catch (error) {
          console.error(error);
        }
      }
    };
    fetchCuentaNombre();
  }, [selectedCuentaId]);

  const handleCuentaChange = (event) => {
    setSelectedCuentaId(event.target.value); // Actualizar el ID de la cuenta seleccionada
    setSelectedCuentaNombre(''); // Limpiar el nombre de la cuenta seleccionada
  };

  return (
    <div className="pagina-principal">
      <NavbarPer idUser={idUser} />

      <div className="cuenta-select">
        <select value={selectedCuentaId} onChange={handleCuentaChange}>
          <option value="">Seleccionar cuenta</option>
          {cuentas.map((cuenta) => (
            <option key={cuenta.id} value={cuenta.id}>
              {cuenta.nombre}
            </option>
          ))}
        </select>
      </div>

      <h1>
        {selectedCuentaId ? `Cuenta: ${selectedCuentaNombre}` : 'Seleccione una cuenta'}
      </h1>

      {selectedCuentaId && (
        <div className="botones">
          <button className={`boton-toggle ${mostrarGastos ? 'activo' : ''}`} onClick={() => setMostrarGastos(true)}>Gastos</button>
          <button className={`boton-toggle ${!mostrarGastos ? 'activo' : ''}`} onClick={() => setMostrarGastos(false)}>Ingresos</button>
        </div>
      )}

      <div className="detalle">
        {selectedCuentaId ? (mostrarGastos ? <PagGastos selectedCuentaId={selectedCuentaId} /> : <PagIngresos selectedCuentaId={selectedCuentaId} />) : null}
      </div>
    </div>
  );
}



